import React, {useEffect, useState} from "react";
import * as S from './styled';
import { RepositoryItem } from "../index";
import useGithub from "../../Hooks/github-hooks";

const Repositories = () => {
    const {githubState, getUserRepos} = useGithub();
    const [hasUserForSearchRepos, setHasUserForSearchRepos] = useState(false);

    useEffect(() => {
        if(!!githubState.user.login){
            getUserRepos();
        }
        setHasUserForSearchRepos(!!githubState.repositories);
    }, [githubState.user.login]);
    return (
        <>
        {hasUserForSearchRepos? (
            <S.Stabs selectedTabClassName="is-selected" selectedTabPanelClassName="is-selected">
                <S.StabList>
                    <S.Stab>Repositories</S.Stab>
                    <S.Stab>Starred</S.Stab>
                </S.StabList>
                <S.StabList>
                    <S.StabPanel>
                    {githubState.repositories.map((item) => (
                    <RepositoryItem 
                    key={item.id} 
                    name={item.name} 
                    linkToRepo={item.owner.html_url} 
                    fullname={item.full_name}/>
                    ))}
                    </S.StabPanel>
                    <S.StabPanel>
                    {githubState.repositories.map(item => (
                    <RepositoryItem 
                    key={item.id} 
                    name="Clone-Netflix" 
                    linkToRepo="https://github.com/R3SP31T4/CloneNetflix" 
                    fullname="R3SP31T4/CloneNetflix
                    "/>
                    ))}
                        <RepositoryItem/>
                    </S.StabPanel>
                </S.StabList>
            </S.Stabs>
        ):(
            <> </>
        )}
        </>


    )
};
export default Repositories;
