const App = () => {
  const {githubState} = useGithub();

// Adicionar animação de carregando

  return (
        <Layout>
          {githubState.hasUser?(
            <>
            <p>Oi</p>
            </>
          ):(
            <NoSearch/>
          )
          }
          </Layout>


  );
}

export default App;
